export default class Preloader{
    static preloadImages({sets,completed}){
        console.log(sets)
        const promises = sets.map(imagePath => Preloader.preloadImage({imagePath}));
        Promise.all(promises).then(completed);
    }

    static preloadImage({imagePath}){
        return new Promise((res,rej) =>{
            let image = new Image();
            image.src = imagePath;
            image.onload = res;
        });
    }

}
