      functions : [
        this.lunchbox,
        this.pencilCase,
        this.makeupBag,
        this.bottleHolder
      ]
    }
    this.bottleHolder = this.bottleHolder.bind(this);
  }

  bottleHolder(){
    this.setState({gallery: bottleHolders});
  }

  lunchbox(){
    this.setState({gallery:lunchBoxes})
  }

  makeupBag(){
    this.setState({gallery:makeupBags})
  }

  pencilCase(){
    this.setState({gallery:pencilCases})
  }

  render(){
    const polaroids = this.state.gallery.map((img)=>{
      return <Polaroid src={img}></Polaroid>
    });
    console.log(polaroids[0])
    return(
      <section className="header">
        <Header functions={this.state.functions} bh ={this.bottleHolder}></Header>  
      </section>  
