import React from 'react';
import './App.scss';
import logo from './assets/images/ROMI1.png'
import bottleHolders from './imgs/bottleHolders';
import lunchBoxes from './imgs/lunchboxes';
import makeupBags from './imgs/makeupBags';
import pencilCases from './imgs/pencilcases';


function Header(props){
  return(
    <section className="header">
        <h1>Accesorios personalizados</h1>
        <div className = "logo">
            <img src={logo} className= "logo-image"></img>
        </div>
    </section>
  );
}

class App extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      imgAlone: false,
      gallery:[],
      functions : [
        this.bottleHolder.bind(this),
        this.lunchbox.bind(this),
        this.makeupBag.bind(this),
        this.pencilCase.bind(this)
      ]
    }
  }

  bottleHolder(){
    this.setState({gallery:bottleHolders})
  }

  lunchbox(){
    this.setState({gallery:lunchBoxes})
  }

  makeupBag(){
    this.setState({gallery:makeupBags})
  }

  pencilCase(){
    this.setState({gallery:pencilCases})
  }

  render(){
    return(
      <Header></Header>
    );
  }
}

export default App;
